import axios from "axios";
export const GET_RECIPES = "GET_RECIPES";

export function getRecipies() {
    return async function (dispatch) {
      let json = await axios.get("'http://localhost:3001/recipes");
      return dispatch({
        type: "GET_RECIPES",
        payload: json.data,
      });
    };
  }
