import React from "react";
import "./paginado.css"

export default function Paginado ({recipesxPage,recipes,paginado, currentPage, setCurrentPage}){
    const pageNumbers= []

    for(let i=0; i<Math.ceil(recipes/recipesxPage);i++){
        pageNumbers.push(i+1)
    }

    function prevPage() {
        setCurrentPage(currentPage - 1);
    }
    function nextPage() {
        setCurrentPage(currentPage + 1);
    }

    return(
        <nav >
            <ul className="paginado">
                <button className="numeros"onClick={prevPage} disabled={currentPage<2}>â—„</button>
                {pageNumbers && pageNumbers.map(n =>(
                   <li key={n} >
                    <button className="numeros" onClick={()=>paginado(n)} disabled={currentPage === n}>{n} </button>
                   </li> 
                ))}
                <button className="numeros" onClick={nextPage} disabled={currentPage >pageNumbers.length-1}>Next</button>
            </ul>
        </nav>
    )
}
