import React from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from "../actions";
import "./recipecard.css"

const RecipeCard = ({id,name,dishtype,diets,image,healthscore})=>{

    return (
        <div className="card">
          <Link to={`/recipes/${id}`}>
          <h3>{name}</h3>
          </Link>
          <img src={image? image : "https://thumbs.dreamstime.com/z/vector-del-icono-de-la-comida-aislado-en-el-fondo-blanco-concepto-logotipo-f-125782725.jpg"} alt={name}></img>
          <p><strong>DishTypes: </strong>{dishtype.map(d=>d[0].toUpperCase() + d.slice(1) + ". ")}</p>
          <p><strong>Diets: </strong>{diets.map(d=>d[0].toUpperCase() + d.slice(1) + ". ")}</p> 
          <p><strong>HealthScore: </strong>{healthscore}</p>
          
        </div>
    )

}

export default RecipeCard;
