import React, {useEffect} from "react";
import { connect } from "react-redux";
import { useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import * as actions from "../actions"
import { steps } from "./auxiliar";
import { Link } from "react-router-dom";

export class RecipeDetail extends React.Component {
    constructor(props){
        super(props)
    }

    

    componentDidMount() {
        this.props.getRecipeDetail(this.props.match.params.id)
     
    }    



    render(){
    return(
      <div>
      <div>
            <Link to="/home">
                <button>Back to Home</button>
            </Link>
        </div>
        <div>
            <h1>{this.props.recipeDetail.name}</h1>
            <img src={this.props.recipeDetail.image}/>
            <h3>{this.props.recipeDetail.dishtype}</h3>
            <h3>{this.props.recipeDetail.healthscore}</h3>
            <p>{this.props.recipeDetail.summary && this.props.recipeDetail.summary.replace(/<[^>]+>/g, "")}</p>
            {/* <p>{this.props.recipeDetail.steps.steps && 
                this.props.recipeDetail.steps.steps.map(i=>i.number + ":" + i.step)}</p> */}
            {/* <p>{this.props.recipeDetail.steps && steps(this.props.recipeDetail.steps)}</p> */}
            
            {/* {infoRecipes.steps?.map((e) => (
            <div className="div-steps">
              <p key={e.number} className="steps">
                <strong>Step {e.number}</strong> - {e.step}
              </p>
            </div> */}
        </div>
        </div>
        
    )}
}

export const mapStateToProps = (state) =>{
    return {recipeDetail: state.recipeDetail}
  }
  
  export const mapDispatchToProps = (dispatch) =>{
    return {
      getRecipeDetail: (id) => dispatch(actions.getRecipeDetail(id)) 
    }
  }

  

export default connect(mapStateToProps,mapDispatchToProps)(RecipeDetail)
