import React, {useState, useEffect} from "react";
import { Link } from "react-router-dom";
import { createRecipe } from "../actions";
import {  useDispatch, useSelector} from "react-redux";
import * as actions from "../actions"


export default function CreateRecipe(){
    let dispatch = useDispatch(); 
    const dietsDB = useSelector((state) => state.diets)
    console.log(dietsDB)

    // useEffect(()=>{
    //     dispatch(actions.getDiets())
    //   },[dispatch]
    //   )

    
    const[state,setState] = useState({
        name:"",
        dishtype: "",
        healthscore: "",
        image:"",
        summary: "",
        steps: "",
        diets: []
    })

    const submitHandler=(e)=>{
        e.preventDefault();
        let rdo = dispatch(createRecipe(state));
        uncheck()
        setState({
            name:"",
            dishtype: "",
            healthscore: "",
            image:"",
            summary: "",
            steps: "",
            diets: []
        })
        alert("Receta creada correctamente")    
    }
    
    let uncheck = function() {
        // let dietas=["Gluten Free","Ketogenic","Vegetarian","Vegan","Pescatarian","Paleo","Primal","lowFodmap","Whole30"]
        for(let i=0; i<dietsDB.length;i++){
            let input = document.getElementById(dietsDB[i]);
            input.checked = false;
        } 
    }
    
    const handleChange=(e)=>{
        setState({
            ...state,
            [e.target.name]:e.target.value
        })
    }

    const handleChangeB=(e)=>{
        if(e.target.checked){
            setState({
                ...state,
                diets:[...state.diets, e.target.name]
            })  
        }else{
            setState({
                ...state,
                diets:state.diets.filter(d => d !== e.target.name)
            })
        }
    }    
     
    return(
        <div>
        <div>
            <Link to="/home">
                <button>Back to Home</button>
            </Link>
        </div>
        <form onSubmit={submitHandler}>
            <h1>Create new recipe</h1>
            <div>
                <label htmlFor="name">Name:</label>
                <input type="text" name="name" value={state.name} onChange={handleChange}></input>
                
                <label htmlFor="dishtype">Type:</label>
                <input type="text" name="dishtype" value={state.dishtype} onChange={handleChange}></input>
               
            </div>
            <div>
                <label htmlFor="healthscore">Healthscore:</label>
                <input type="text" name="healthscore" value={state.healthscore} onChange={handleChange} ></input>
                
                <label htmlFor="image">Image:</label>
                <input type="text" name="image" placeholder="URL" value={state.image} onChange={handleChange} ></input>
                
            </div>
            <div>
                <label htmlFor="summary">Summary:</label>
                <input type="text" size="80" name="summary" value={state.summary} onChange={handleChange}></input>
                
            </div>
            <div>
                <label htmlFor="steps">Steps:</label>
                <input type="text" size="80" name="steps" value={state.steps} onChange={handleChange}></input>
               
            </div>

            
            <div>
                <legend>Diets:</legend>
                
                <div>
                    <input type="checkbox" id="Gluten Free" name="gluten free"  onChange={handleChangeB}/>
                    <label for="Gluten Free">Gluten Free</label>
                </div>
                <div>
                    <input type="checkbox" id="Ketogenic" name="ketogenic"  onChange={handleChangeB} />
                    <label for="Ketogenic">Ketogenic</label>
                </div>
                <div>
                    <input type="checkbox" id="Vegetarian" name="lacto ovo vegetarian" onChange={handleChangeB}/>
                    <label for="Vegetarian">Vegetarian</label>
                </div>
                
                <div>
                    <input type="checkbox" id="Vegan" name="vegan" onChange={handleChangeB}/>
                    <label for="Vegan">Vegan</label>
                </div>
                <div>
                    <input type="checkbox" id="Pescatarian" name="pescatarian" onChange={handleChangeB} />
                    <label for="Pescetarian">Pescetarian</label>
                </div>
                <div>
                    <input type="checkbox" id="Paleo" name="paleolithic" onChange={handleChangeB} />
                    <label for="Paleo">Paleo</label>
                </div>
                    <input type="checkbox" id="lowFodmap" name="lowfodmap" onChange={handleChangeB}/>
                    <label for="Low FODMAP">Low FODMAP</label>
                <div></div>
                <div>
                    <input type="checkbox" id="Primal" name="primal" onChange={handleChangeB}/>
                    <label for="Primal">Primal</label>
                </div>
                <div>
                    <input type="checkbox" id="Whole30" name="whole 30" onChange={handleChangeB}/>
                    <label for="Whole30">Whole30</label>
                </div>
            <div>
                If you don´t know which diet your recipe corresponds to, please check <a href="https://spoonacular.com/food-api/docs#Diets">here</a>
            </div>

            </div>
            <button type="submit">Create</button>
            
            
        </form>
        </div>
    )
        
    
}
