import {GET_RECIPES, GET_RECIPE_DETAIL, FILTER_BY_DIET} from "../actions";

const initialState = {
    recipes: [],
    recipeDetail: {}
}

function rootReducer (state=initialState,action){
 switch(action.type){
    case GET_RECIPES:
        return{
            ...state,
            recipes:action.payload
        }
    case GET_RECIPE_DETAIL:
        return{
            ...state,
            recipeDetail: action.payload
        }
