import React from "react";
import { Link } from "react-router-dom";
import * as actions from "../actions";
import {  useDispatch } from "react-redux";
import { useState} from "react";

export default function SearchBar ({setCurrentPage, setOrden}){
    const pageNumbers= []

    let dispatch = useDispatch(); 

    

    const handleFilterxDiet = function(e){
        e.preventDefault();
        dispatch(actions.filterbyDiet(e.target.value))
        // setCurrentPage(1);
        // setOrden(`Ordenado ${e.target.value}`)
    }

    const handleOrderbyName = function(e){
        e.preventDefault();
        dispatch(actions.orderByName(e.target.value));
        // setCurrentPage(1);
        // setOrden(`Ordenado ${e.target.value}`)

    }

    const handleOrderbyScore = function(e){
        e.preventDefault();
        dispatch(actions.orderByScore(e.target.value));
        setCurrentPage(1);
        setOrden(`Ordenado ${e.target.value}`)

    }

    
    return(
        <nav>
            <div>
                <input placeholder="enter recipeÂ´s name..."></input>
                <button>Search</button>
            </div>
            <div>
            <Link to="/create">
                <button>Create new recipe</button>
            </Link>
                
            </div>
    
            <select name="Diet" onChange={handleFilterxDiet} >
                <option>----Select a Diet----</option>
                <option value = "All">Todas</option>
                <option value = "gluten free">Gluten Free</option>
                <option value = "ketogenic">Ketogenic</option>
                <option value = "lacto ovo vegetarian">Lacto Ovo Vegetarian</option>
                {/* <option value = "Lacto-Vegetarian">Lacto-Vegetarian</option>
                <option value = "Ovo-Vegetarian">Ovo-Vegetarian</option> */}
                <option value = "vegan">Vegan</option>
                <option value = "pescatarian">Pescatarian</option>
                <option value = "paleolithic">Paleo</option>
                <option value = "Primal">Primal</option>
                <option value = "lowfodmap">Low FODMAP</option>
                <option value = "whole 30">Whole30</option>
            </select>
            <select name = "Alfabetic" onChange={handleOrderbyName}>
                <option>----Alfabetic Order----</option>
                <option value = "A-Z">A-Z</option>
                <option value = "Z-A">Z-A</option>
            </select>
            <select name = "Health-Score" onChange={handleOrderbyScore}>
                <option>----Health-Score Order----</option>
                <option value = "Asc">Ascendent</option>
                <option value = "Des">Descendent</option>
            </select>

            
        </nav>
    )
}
