import React from "react";
import { connect } from "react-redux";
import { useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import * as actions from "../actions"

function RecipeDetail(props){
    console.log(props)
    let dispatch = useDispatch();
    let {id} = useParams();
    let detail = useSelector (state => state.recipeDetail);
    console.log(id);
    console.log(detail);

    React.useEffect(()=>{
        dispatch(actions.getRecipeDetail(id))
      },[]
      )


    return(
        <div>
            <h1>detalle:{detail[0].nombre}</h1>
            <p>{detail[0].imagen}</p>
            <p>{detail[0].tipodeplato}</p>
            <p>{detail[0].healthScore}</p>
            <p>{detail[0].resumen}</p>
            <p>{detail[0].dieta}</p>
            <p>{detail[0].pasoapaso}</p>


        </div>
    )
        
    
}

export default RecipeDetail
