import {GET_RECIPES, GET_RECIPE_DETAIL, ADD_RECIPE} from "../actions";

const initialState = {
    recipes: [],
    recipeDetail: {},
    myRecipies:[],
}

function rootReducer (state=initialState,action){
 switch(action.type){
    case GET_RECIPES:
        return{
            ...state,
            recipes:action.payload
        }
    case GET_RECIPE_DETAIL:
        return{
            ...state,
            recipeDetail: action.payload
        }
    // case ADD_RECIPE:
    //     return{


    //     }
    
    default:
        return{...state}
 }
}

export default rootReducer
