import React from "react";
import { Link } from "react-router-dom";
import RecipeCard from "../components/RecipeCard"
import * as actions from "../actions";
import { useDispatch, useSelector } from "react-redux";
import { connect } from "react-redux";
import { useState, useEffect } from "react";
import { render } from "react-dom";

export class Home extends Component {
    constructor(props){
        super(props)
    }

    // const dispatch = useDispatch();
    // const allRecipes = useSelector((state)=>state.recipies)
    // useEffect(()=>{
    //     return ()=>this.props.getRecipies()
    // },[])

    componentDidMount() {
        this.props.getRecipies()
      }


    render(){
    return(
        <div>
            <h1>estas en la home</h1>
            <div>
                <input placeholder="enter recipeÂ´s name..."></input>
                <button>Search</button>
            </div>
            <div>
                <button>Create new recipe</button>
            </div>
    
            <select name="Diet">
                <option>----Select a Diet----</option>
                <option value = "All">Todas</option>
                <option value = "GF">Gluten Free</option>
                <option value = "Keto">Ketogenic</option>
                <option value = "Veg">Vegetarian</option>
                <option value = "Lacto-V">Lacto-Vegetarian</option>
                <option value = "Ovo-V">Ovo-Vegetarian</option>
                <option value = "Vegan">Vegan</option>
                <option value = "Pes">Pescetarian</option>
                <option value = "Paleo">Paleo</option>
                <option value = "Primal">Primal</option>
                <option value = "LOWF">Low FODMAP</option>
                <option value = "W30">Whole30</option>
            </select>
            <select name = "Alfabetic">
                <option>----Alfabetic Order----</option>
                <option value = "Asc">Ascendent</option>
                <option value = "Des">Descendent</option>
            </select>
            <select name = "Health-Score">
                <option>----Health-Score Order----</option>
                <option value = "Asc">Ascendent</option>
                <option value = "Des">Descendent</option>
            </select>

            <div>{this.props.recipies.map((recipe) => {return (
                <RecipeCard  name={recipe.nombre} 
                tipodedieta={recipe.dieta} 
                tipo={recipe.tipodeplato} 
                image={recipe.imagen}/>)})}
            </div>

        </div>
        
    )}
}

export const mapStateToProps = (state) =>{
    return {recipies: state.recipies}
  }
  
  export const mapDispatchToProps = (dispatch) =>{
    return {
      getRecipies: () => dispatch(actions.getRecipies()) 
    }
  }

export default connect(mapStateToProps,mapDispatchToProps)(Home)
