import {GET_RECIPES, GET_RECIPE_DETAIL, FILTER_BY_DIET} from "../actions";

const initialState = {
    recipes: [],
    recipeDetail: {}
}

function rootReducer (state=initialState,action){
 switch(action.type){
    case GET_RECIPES:
        return{
            ...state,
            recipes:action.payload
        }
    case GET_RECIPE_DETAIL:
        return{
            ...state,
            recipeDetail: action.payload
        }
    case FILTER_BY_DIET:
        const recipes = state.recipes;
        console.log(action.payload)
        const filtered = action.payload === "All" ? recipes : recipes.filter(r=>r.status === action.payload)
        return{
            ...state,
            recipes: filtered
        }
        

    default:
        return{...state}
 }
}

export default rootReducer
