import React, {useState, useEffect} from "react";
import { Link } from "react-router-dom";
import { createRecipe } from "../actions";
import {  useDispatch, useSelector} from "react-redux";
import * as actions from "../actions"
import { validation } from "./auxiliar";
import "./createrecipe.css"




export default function CreateRecipe(){
    let dispatch = useDispatch(); 
    const dietsDB = useSelector((state) => state.diets)
    console.log(dietsDB)

    useEffect(()=>{
        dispatch(actions.getDiets())
      },[dispatch]
      )

    
    const[state,setState] = useState({
        name:"",
        dishtype: "",
        healthscore: "",
        image:"",
        summary: "",
        steps: "",
        diets: []
    })

    const [errors, setErrors] = useState({
        name: "",
        summary: "",
        healthscore: "",
    })

    const submitHandler=(e)=>{
        e.preventDefault();
        if (validation()){
            let rdo = dispatch(createRecipe(state));
            uncheck()
            setState({
                name:"",
                dishtype: "",
                healthscore: "",
                image:"",
                summary: "",
                steps: "",
                diets: []
            })
            alert("Receta creada correctamente")
        }else{
        alert ("name/summary required -- healthscore 0 to 100 -- at least 1 diet selected")}
    }
    
    let uncheck = function() {
        // let dietas=["Gluten Free","Ketogenic","Vegetarian","Vegan","Pescatarian","Paleo","Primal","lowFodmap","Whole30"]
        for(let i=0; i<dietsDB.length;i++){
            let input = document.getElementById(dietsDB[i].name);
            input.checked = false;
        } 
    }
    
    const handleChange=(e)=>{
        setState({
            ...state,
            [e.target.name]:e.target.value
        })
        // setErrors(validation({
        //     ...state,
        //     // [e.target.name]:e.target.value
        // }))
        // console.log(errors)
    }

    const handleChangeB=(e)=>{
        if(e.target.checked){
            setState({
                ...state,
                diets:[...state.diets, e.target.name]
            })  
        }else{
            setState({
                ...state,
                diets:state.diets.filter(d => d !== e.target.name)
            })
        }
    }  
    
    let validateName = ()=>{
        if(!state.name){setErrors ({...errors,name:"Name is required"})}else{setErrors ({...errors,name:""})}}

    let validateSummary = ()=>{
        if(!state.summary){setErrors ({...errors,summary :"Summary is required"})}else{setErrors ({...errors,summary :""})}}
    
    let validateHS = ()=>{
        if(state.healthscore<0 || state.healthscore>100){setErrors ({...errors,healthscore : "Between 0 and 100"})}else{setErrors ({...errors,healthscore : ""})}}
    
    let validation = ()=>{
        if (state.name && state.summary && parseInt(state.healthscore)<=100 && parseInt(state.healthscore)>=0 && state.diets.length>0){
        return true 
        }
        return false
    }
    
//return alert ("name/summary required -- healthscore 0 to 100 -- at least 1 diet selected")
     
    return(
        <div>
        <div className="header">
            <Link className="back"to="/home">
                <button className="btnback">Back to Home</button>
            </Link>
            <h1>Create new recipe</h1>
        </div>
        <form className="form" onSubmit={submitHandler}>
            
            <div className="div">
                <label htmlFor="name">Name *:  </label>
                <input type="text" name="name" value={state.name} onChange={handleChange} onBlur={validateName}></input>
                {errors.name && (
                    <p>{errors.name}</p>
                )}
                
                <label className="input" htmlFor="dishtype">Type:  </label>
                <input className="input"type="text" name="dishtype" value={state.dishtype} onChange={handleChange} onClick={validateName}></input>
               
            </div>
            <div className="div">
                <label htmlFor="healthscore">Healthscore:  </label>
                <input type="text" name="healthscore" value={state.healthscore} onChange={handleChange} onBlur={validateHS}></input>
                {errors.healthscore && (
                    <p>{errors.healthscore}</p>
                )}
                
                <label htmlFor="image">Image:  </label>
                <input type="text" name="image" placeholder="URL" value={state.image} onChange={handleChange} onClick={validateHS}></input>
                
            </div>
            <div className="div">
                <label htmlFor="summary">Summary *:  </label>
                <input type="text" size="80" name="summary" value={state.summary} onChange={handleChange} onBlur={validateSummary}></input>
                {errors.summary && (
                    <p>{errors.summary}</p>
                )}
                
            </div>
            <div className="div">
                <label htmlFor="steps">Steps:</label>
                <input type="text" size="80" name="steps" value={state.steps} onChange={handleChange} onClick={validateSummary}></input>
               
            </div>

            
            <div >
                <legend>Diets :</legend>

                <div>{ dietsDB.map(i=>(
                    <div key={i.id}>
                    <input type="checkbox" id={i.name} name={i.name}  onChange={handleChangeB}/>
                    <label for={i.name}>{i.name.toUpperCase()}</label>
                    </div> 
                    ))}
                </div>
                
                {/* <div>
                    <input type="checkbox" id="Gluten Free" name="gluten free"  onChange={handleChangeB}/>
                    <label for="Gluten Free">Gluten Free</label>
                </div>
                <div>
                    <input type="checkbox" id="Ketogenic" name="ketogenic"  onChange={handleChangeB} />
                    <label for="Ketogenic">Ketogenic</label>
                </div>
                <div>
                    <input type="checkbox" id="Vegetarian" name="lacto ovo vegetarian" onChange={handleChangeB}/>
                    <label for="Vegetarian">Vegetarian</label>
                </div>
                
                <div>
                    <input type="checkbox" id="Vegan" name="vegan" onChange={handleChangeB}/>
                    <label for="Vegan">Vegan</label>
                </div>
                <div>
                    <input type="checkbox" id="Pescatarian" name="pescatarian" onChange={handleChangeB} />
                    <label for="Pescetarian">Pescetarian</label>
                </div>
                <div>
                    <input type="checkbox" id="Paleo" name="paleolithic" onChange={handleChangeB} />
                    <label for="Paleo">Paleo</label>
                </div>
                    <input type="checkbox" id="lowFodmap" name="lowfodmap" onChange={handleChangeB}/>
                    <label for="Low FODMAP">Low FODMAP</label>
                <div></div>
                <div>
                    <input type="checkbox" id="Primal" name="primal" onChange={handleChangeB}/>
                    <label for="Primal">Primal</label>
                </div>
                <div>
                    <input type="checkbox" id="Whole30" name="whole 30" onChange={handleChangeB}/>
                    <label for="Whole30">Whole30</label>
                </div> */}
            <div>
                If you don´t know which diet your recipe corresponds to, please check <a href="https://spoonacular.com/food-api/docs#Diets">here</a>
            </div>
            
            </div>
            <button className="btncreate" type="submit" id= "submit"  >Create</button>
            
            
        </form>
        </div>
    )
        
    
}
