export function diets(d) {
    if (typeof d === "string"){
        return d
    }else{
        return d.name
    }
  }

export function validation(input){
    let errors = {}
    if(!input.name){errors.name = "Name is required"}
    if(!input.summary){errors.summary = "Summary is required"}
    if(!input.diets){errors.diets = "Select at least one diet"}
    if (parseInt(input.healthscore)<0 || parseInt(input.healthscore)>100){errors.healthscore = "Between 0 and 100"}



    return errors
}
