import {GET_RECIPES, GET_RECIPE_DETAIL, FILTER_BY_DIET, ORDER_BY_NAME} from "../actions";

const initialState = {
    recipes: [],
    recipeDetail: {}
}

function rootReducer (state=initialState,action){
 switch(action.type){
    case GET_RECIPES:
        return{
            ...state,
            recipes:action.payload
        }
    case GET_RECIPE_DETAIL:
        return{
            ...state,
            recipeDetail: action.payload
        }
    case FILTER_BY_DIET:
        const recipes = state.recipes;
        const filtered = action.payload === "All" ? recipes : recipes.filter(r =>{
            if(typeof r.diets[0]=== "string") {return r.diets.includes(action.payload.toLowerCase())}
            else{return r.diets.map(s=>s.name).includes(action.payload.toLowerCase())}})    
        return{
            ...state,
            recipes: filtered
        }
    
    case ORDER_BY_NAME:
        const recipess = state.recipes;
        if (action.payload === "A-Z"){
            recipess.sort((a,b) => (a.name > b.name) ? 1 : ((b.name > a.name) ? -1 : 0))
        }else{recipess.sort((a,b) => (a.name < b.name) ? 1 : ((b.name < a.name) ? -1 : 0))}
        return{
            ...state,
            recipes: recipess
        }

        

    default:
        return{...state}
 }
}


export default rootReducer
