const Home = ()=>{
    [currentPage,setCurrentPage]=useState(1);
    [characterxPage,setCharacterxPage]=useState(9);
    const indexLastCharacter = currentPage * characterxPage;
    const indexFirstCharacter = indexLastCharacter - characterxPage;
    const currentCharacters= recipes.slice(indexFirstCharacter,indexLastCharacter)

    const paginado = (number)=>{
        setCurrentPage(number)
    }

    const dispatch = useDispatch();
   
    const recipes = useSelector ((state) => state.recipes);
   
    useEffect(()=>{
        dispatch(actions.getRecipes())
    },[dispatch, recipes])
    

    return (
        <div>
            <h1>estas en la home</h1>
            <div>
                <input placeholder="enter recipe´s name..."></input>
                <button>Search</button>
            </div>
            <div>
            <Link to="/create">
                <button>Create new recipe</button>
            </Link>
                
            </div>
    
            <select name="Diet">
                <option>----Select a Diet----</option>
                <option value = "All">Todas</option>
                <option value = "GF">Gluten Free</option>
                <option value = "Keto">Ketogenic</option>
                <option value = "Veg">Vegetarian</option>
