import {GET_RECIPES, GET_RECIPE_DETAIL, FILTER_BY_DIET} from "../actions";

const initialState = {
    recipes: [],
    recipeDetail: {}
}

function rootReducer (state=initialState,action){
 switch(action.type){
    case GET_RECIPES:
        return{
            ...state,
            recipes:action.payload
        }
    case GET_RECIPE_DETAIL:
        return{
            ...state,
            recipeDetail: action.payload
        }
    case FILTER_BY_DIET:
        const recipes = state.recipes;
        const filtered = action.payload === "All" ? recipes : recipes.filter(r =>{
            if(typeof r.diets[0]=== "string") {r.diets.includes(action.payload.toLowerCase())}
            else{r.diets.filter(r=>r.name).includes(action.payload.toLowerCase())}})

        console.log(recipes)
        console.log(filtered)
        return{
            ...state,
            recipes: filtered
        }
        

    default:
        return{...state}
 }
}

export default rootReducer
